import React, { useState, useEffect } from 'react';
import api from './api'; // ✅ Central axios instance

function App() {
  const [user, setUser] = useState(null);
  const [files, setFiles] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await api.get('/user');
        setUser(response.data);

        // ✅ Trigger CSRF token generation
        await api.get('/csrf-token');

        fetchFiles();
      } catch (err) {
        if (err.response?.status === 401) {
          setUser(null);
        } else {
          console.error('Error fetching user:', err);
          setError('Error fetching user');
        }
      }
    };

    fetchUser();
  }, []);

  const fetchFiles = async () => {
    try {
      setLoading(true);
      const response = await api.get('/drive/files');
      const data = typeof response.data === 'string' ? JSON.parse(response.data) : response.data;
      setFiles(data.files || []);
    } catch (err) {
      console.error('Error fetching files:', err);
      setError('Error fetching files');
    } finally {
      setLoading(false);
    }
  };

  const handleFileUpload = async (e) => {
    e.preventDefault();
    if (!selectedFile) return;

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      await api.post('/drive/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setSelectedFile(null);
      fetchFiles();
    } catch (err) {
      console.error('Error uploading file:', err);
      setError('Error uploading file');
    }
  };

  const handleDelete = async (fileId) => {
    try {
      // 🔄 Step 1: Refresh CSRF token to avoid using a rotated one
      await api.get('/csrf-token');

      // 🔍 Step 2: Log what Spring expects
      const debugRes = await api.get('/debug-csrf');
      const expected = debugRes.data.expected;
      console.log('✅ Spring expects token:', expected);

      // 🔍 Step 3: Log what frontend is actually sending
      const cookieToken = document.cookie.match(/XSRF-TOKEN=([^;]+)/)?.[1];
      console.log('🍪 Cookie token:', cookieToken);

      // 🔄 Step 4: Proceed with delete
      await api.post(`/drive/files/delete/${fileId}`, {}); // CSRF-safe POST
      fetchFiles();
    } catch (err) {
      console.error('❌ Error deleting file:', err);
      setError('Error deleting file');
    }
  };


  const handleDownload = async (fileId) => {
    try {
      const response = await api.get(`/drive/files/download/${fileId}`, {
        responseType: 'blob',
      });

      const blob = new Blob([response.data], {
        type: response.headers['content-type'],
      });

      let filename = 'downloadedFile';
      const disposition = response.headers['content-disposition'];
      if (disposition && disposition.includes('filename=')) {
        const match = /filename[^;=\n]*=((['"]).*?\2|[^;\n]*)/.exec(disposition);
        if (match && match[1]) {
          filename = match[1].replace(/['"]/g, '');
        }
      }

      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', filename);
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);
    } catch (err) {
      console.error('Error downloading file:', err);
      setError('Error downloading file');
    }
  };

  const handleLogout = async () => {
    try {
      await api.get('/csrf-token'); // refresh token before logout
      await api.post('/logout', {});
      setUser(null);
      setFiles([]);
      setSelectedFile(null);
      setError('');
      window.location.href = 'http://localhost:3000';
    } catch (err) {
      console.error('Logout failed:', err);
    }
  };

  return (
    <div style={{ margin: '20px' }}>
      <h1>DriveSync App</h1>

      {user ? (
        <>
          <p>Welcome, {user.name || user.email || 'User'}!</p>

          <button onClick={handleLogout}>Logout</button>
          <button onClick={fetchFiles} style={{ marginLeft: '10px' }}>Refresh Files</button>

          {error && <p style={{ color: 'red' }}>{error}</p>}

          <h2>Your Google Drive Files</h2>
          {loading ? (
            <p>Loading files...</p>
          ) : files.length ? (
            <ul>
              {files.map((file) => (
                <li key={file.id}>
                  {file.name}{' '}
                  <button onClick={() => handleDelete(file.id)}>Delete</button>
                  <button onClick={() => handleDownload(file.id)}>Download</button>
                </li>
              ))}
            </ul>
          ) : (
            <p>No files found.</p>
          )}

          <h2>Upload a File</h2>
          <form onSubmit={handleFileUpload}>
            <input
              type="file"
              onChange={(e) => setSelectedFile(e.target.files[0])}
            />
            <button type="submit">Upload</button>
          </form>
        </>
      ) : (
        <div>
          <p>You are not logged in.</p>
          <button
            onClick={() =>
              window.location.href = 'http://localhost:8080/oauth2/authorization/google'
            }
          >
            Login with Google
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
