import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [user, setUser] = useState(null);
  const [files, setFiles] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // Fetch user on mount
  useEffect(() => {
    fetchUser();
  }, []);

  // Fetch authenticated user details
  const fetchUser = async () => {
    try {
      const response = await axios.get('http://localhost:8080/user', {
        withCredentials: true,
      });
      setUser(response.data);
      fetchFiles(); // only fetch if logged in
    } catch (err) {
      if (err.response?.status === 401) {
        setUser(null); // not logged in
      } else {
        console.error("❌ Error fetching user:", err);
        setError('Failed to fetch user.');
      }
    }
  };

  // Fetch Google Drive files
  const fetchFiles = async () => {
    try {
      setLoading(true);
      const response = await axios.get('http://localhost:8080/drive/files', {
        withCredentials: true,
      });
      const data = typeof response.data === 'string' ? JSON.parse(response.data) : response.data;
      setFiles(data.files || []);
      setError('');
    } catch (err) {
      console.error('❌ Error fetching files:', err);
      setError('Error fetching files. Are you authenticated?');
    } finally {
      setLoading(false);
    }
  };

  // Upload a file
  const handleFileUpload = async (e) => {
    e.preventDefault();
    if (!selectedFile) return;

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      await axios.post('http://localhost:8080/drive/upload', formData, {
        withCredentials: true,
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setSelectedFile(null);
      fetchFiles();
    } catch (err) {
      console.error('❌ Error uploading file:', err);
      setError('Error uploading file');
    }
  };

  // Delete a file
  const handleDelete = async (fileId) => {
    try {
      await axios.delete(`http://localhost:8080/drive/files/${fileId}`, {
        withCredentials: true,
      });
      fetchFiles();
    } catch (err) {
      console.error('❌ Error deleting file:', err);
      setError('Error deleting file');
    }
  };

  // Download a file
  const handleDownload = async (fileId) => {
    try {
      const response = await axios.get(
        `http://localhost:8080/drive/files/download/${fileId}`,
        { withCredentials: true, responseType: 'blob' }
      );

      const blob = new Blob([response.data], {
        type: response.headers['content-type'],
      });

      let filename = 'downloadedFile';
      const disposition = response.headers['content-disposition'];
      if (disposition && disposition.includes('filename=')) {
        const match = /filename[^;=\n]*=((['"]).*?\2|[^;\n]*)/.exec(disposition);
        if (match && match[1]) {
          filename = match[1].replace(/['"]/g, '');
        }
      }

      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', filename);
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);
    } catch (err) {
      console.error('❌ Error downloading file:', err);
      setError('Error downloading file');
    }
  };

  // ✅ Logout and clear everything
  const handleLogout = async () => {
    try {
      await axios.get("http://localhost:8080/logout", {
        withCredentials: true,
      });

      // Clear UI state
      setUser(null);
      setFiles([]);
      setSelectedFile(null);
      setError('');

      // Re-check session (will get 401 if logged out)
      fetchUser();
    } catch (err) {
      console.error("❌ Logout failed:", err);
    }
  };

  return (
    <div style={{ margin: '20px' }}>
      <h1>DriveSync App</h1>

      {user ? (
        <>
          <p>Welcome, {user.name || user.email || 'User'}!</p>

          <button onClick={handleLogout}>Logout</button>
          <button onClick={fetchFiles} style={{ marginLeft: '10px' }}>Refresh Files</button>

          {error && <p style={{ color: 'red' }}>{error}</p>}

          <h2>Your Google Drive Files</h2>
          {loading ? (
            <p>Loading files...</p>
          ) : files.length ? (
            <ul>
              {files.map((file) => (
                <li key={file.id}>
                  {file.name}{' '}
                  <button onClick={() => handleDelete(file.id)}>Delete</button>
                  <button onClick={() => handleDownload(file.id)}>Download</button>
                </li>
              ))}
            </ul>
          ) : (
            <p>No files found.</p>
          )}

          <h2>Upload a File</h2>
          <form onSubmit={handleFileUpload}>
            <input type="file" onChange={(e) => setSelectedFile(e.target.files[0])} />
            <button type="submit">Upload</button>
          </form>
        </>
      ) : (
        <div>
          <p>You are not logged in.</p>
          <button
            onClick={() =>
              window.location.href = 'http://localhost:8080/oauth2/authorization/google'
            }
          >
            Login with Google
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
